export abstract class PageClass {
  /*
   * Method used for mapping complex objects from http response
   */
  // eslint-disable-next-line @typescript-eslint/explicit-module-boundary-types
  toMap(object): Map<any, any> {
    if (object) {
      const mappedObj = new Map();
      try {
        // if there is an array of objects
        for (const element of object) {
          for (const [key, value] of Object.entries(element)) {
            mappedObj.set(key, value);
          }
        }
      } catch (e) {
        // if object is not iterable
        for (const [key, value] of Object.entries(object)) {
          mappedObj.set(key, value);
        }
      }
      return mappedObj;
    } else {
      return new Map();
    }
  }
}

import { takeUntil } from 'rxjs/operators';
import { WindowService } from '../services/browser/window.service';
import { Subject } from 'rxjs';

export abstract class WindowWidth {
  public subscription$ = new Subject();
  public isMobile: boolean;
  public isTouchDevice = false;
  public breakpoints = {
    desktop: 1199.98,
  };

  constructor(public windowService: WindowService) {
    if ('ontouchstart' in document.documentElement) {
      this.isTouchDevice = true;
    }
  }

  protected initWindowWidth(callback: any): void {
    this.windowService
      .setWindowWidth()
      .pipe(takeUntil(this.subscription$))
      .subscribe((width) => {
        this.isMobile = width <= this.breakpoints.desktop;
        callback();
      });
  }

  protected detectWindowWidth(callback: any): void {
    this.windowService.windowWidth$
      .pipe(takeUntil(this.subscription$))
      .subscribe((width) => {
        this.isMobile = width <= this.breakpoints.desktop;
        callback();
      });
  }
}

