  width: 100%;
  color: $color-borders;
  position: fixed;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 2;
  background-color: rgba($color-general-dark, 0.15);
}

.menu__panel {
  height: 100%;
  padding: 0 0 30px 0;
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  z-index: 1;
  background-color: $color-white;
  box-shadow: -1px 0 4px 0 rgba(5, 5, 5, 0.3);

