    width: 320px;
  }
  @include resolution_smartphone {
    width: 260px;
  }

  .panel__header {
    width: 100%;
    height: $header-height;
    background-color: $color-general-light;
    margin-bottom: 15px;
  }

  .menu__links {
    width: 100%;
    padding: 0 15px 0 10px;

    .menu__link {
      display: block;
      width: 100%;
      font-size: $font-size-bigger;
      font-weight: $font-weight-medium;

      a {
        position: relative;
        display: flex;
        border-radius: $border-radius-medium;
        padding: 12px 10px 10px;

        .fa {
          margin: 2px 8px 0 0;
        }

        &:link,
        &:visited {
          color: $color-black;
        }

        &:hover {
          color: rgba($color-general, 0.85);
          background-color: rgba($color-general, 0.08);
        }

        &:active {
          color: rgba($color-general, 0.75);
          background-color: rgba($color-general, 0.15);
        }

        &.current {
          color: $color-general;
          background-color: rgba($color-general, 0.15);
        }
      }
    }

    .menu__link + .menu__link {
      margin-top: 4px;
    }
  }
}

